const initState = {
  items: {},
  totalPrice: 0,
  totalItems: 0,
};

function basket(state = initState, action) {
  switch (action.type) {
    case "ADD_PRODUCT_BASKET": {
      const newObj = {
        ...state.items,
        [action.payload.id]: {
            items: !state.items[action.payload.id]
            ? [action.payload]
            : [...state.items[action.payload.id], action.payload],
            totalCost: 1000,
        },
      };
      const arrProduct = [].concat.apply([], Object.values(newObj));
      const totalPrice = arrProduct.reduce((sum, obj) => obj.cost + sum, 0);
      return {
        ...state,
        items: newObj,
        totalItems: arrProduct.length,
        totalPrice,
      };
    }
    default:
      return state;
  }
}

export default basket;
