import React from "react";
import Header from "../Header/Header";
import "../basket/Basket.css";
import { withRouter } from "react-router-dom";
import BasketItem from "../BasketItem/BasketItem";
import { useBasket } from "./useBasket";
function Basket() {
  const { items, totalItems, addedProducts } = useBasket();
  return (
    <div>
      <Header />
      {totalItems ? (
        <div className="wrapper">
          {addedProducts.map((obj) => (
            <BasketItem 
            name={obj.name} 
            totalPrice={items[obj.id].totalPrice}
            totalItems={items[obj.id].items.length} />
          ))}
          <BasketItem name="IPhone Xr" />
          <div className="order">
            <div className="email">
              <h4>Email</h4>
              <input
                className="input"
                type="email"
                placeholder="email@example.com"
              />
            </div>
            <div className="password">
              <h4>Password</h4>
              <input className="input" type="pasword" placeholder="Password" />
            </div>
          </div>
        </div>
      ) : (
        <h1>Корзина пуста, добавьте какой-либо товар :)</h1>
      )}
    </div>
  );
}

export default withRouter(Basket);
