const initState = {
    items: [1, 2, 3],
    isLoaded: false,
  };

  function produts(state = initState, action) {
    switch (action.type) {
      case "SET_PRODUCTS":
        return { ...state, items: action.payload };
      default:
        return state;
    }
  }

  export default produts;
